import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import "../styles/Home.css";

const titles = ["UI/UX Designer", "Student", "Full Stack Web Developer"];

const Home = () => {
  const [currentTitle, setCurrentTitle] = useState(titles[0]);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTitle(titles[Math.floor(Math.random() * titles.length)]);
    }, 2000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="home-container">
      <h1 className="intro-text">Hey there, I'm</h1>
      <h1 className="name-text">Jose Chemmanoor</h1>
      <motion.p
        className="dynamic-title"
        key={currentTitle}
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: -20 }}
        transition={{ duration: 0.5 }}
      >
        {currentTitle}
      </motion.p>
    </div>
  );
};

export default Home;
