import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import { Link } from "react-router-dom";
import "../styles/Home.css";

const titles = ["UI/UX Designer", "Front-End Developer", "Student"];

const pageVariants = {
  initial: { opacity: 0, y: 50 },
  animate: { opacity: 1, y: 0, transition: { duration: 0.6 } },
  exit: { opacity: 0, y: -50, transition: { duration: 0.6 } },
};

const Home = () => {
  const [animatedTitle, setAnimatedTitle] = useState(titles[0]);

  useEffect(() => {
    const interval = setInterval(() => {
      const randomTitle = titles[Math.floor(Math.random() * titles.length)];
      setAnimatedTitle(randomTitle);
    }, 1500); // Change title every 1.5 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <motion.div
      className="home-container"
      variants={pageVariants}
      initial="initial"
      animate="animate"
      exit="exit"
    >
      <h1>
        Hey there, I'm <span className="name">Jose Chemmanoor</span>
      </h1>
      <motion.p
        key={animatedTitle}
        className="animated-title"
        initial={{ opacity: 0, y: -10 }}
        animate={{ opacity: 1, y: 0, transition: { duration: 0.5 } }}
        exit={{ opacity: 0, y: 10, transition: { duration: 0.5 } }}
      >
        {animatedTitle}
      </motion.p>
      <Link to="/about" className="neon-button">
        Get to Know More About Me
      </Link>
    </motion.div>
  );
};

export default Home;
