import React, { useEffect, useState } from "react";
import "../styles/Home.css";

function Home() {
  const titles = ["UI/UX Designer", "Student", "Full-Stack Web Developer"];
  const [currentTitle, setCurrentTitle] = useState(titles[0]);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTitle((prevTitle) => {
        const nextIndex = (titles.indexOf(prevTitle) + 1) % titles.length;
        return titles[nextIndex];
      });
    }, 2000);

    return () => clearInterval(interval);
  }, []);

  return React.createElement(
    "section",
    { id: "home", className: "home" },
    React.createElement("p", { className: "intro" }, "Hey there, I'm"),
    React.createElement(
      "h1",
      { className: "name" },
      "Jose Chemmanoor"
    ),
    React.createElement(
      "h2",
      { className: "changing-title" },
      currentTitle
    )
  );
}

export default Home;
