import React from "react";
import { motion } from "framer-motion";
import "../styles/About.css";
import myPhoto from "../assets/myphoto.jpg";

const AboutPage = () => {
  return (
    <motion.section
      className="about"
      initial={{ opacity: 0, y: 50 }}
      animate={{ opacity: 1, y: 0, transition: { duration: 0.6 } }}
      exit={{ opacity: 0, y: -50, transition: { duration: 0.6 } }}
    >
      <div className="about-content">
        <h1>About Me</h1>
        <p>
          Welcome to my digital space! I'm <strong>Jose Chemmanoor</strong>, a passionate BTech student specializing in Computer Science and Engineering at <strong>Rajagiri School of Engineering and Technology (RSET)</strong> in Kakkanad, Kochi.
          <br /><br />
          While I currently call Kochi home, my journey began in the bustling city of <strong>Thrissur, Kerala</strong>. I pursued my early education at <strong>Don Bosco ICSE School, Mannuthy</strong>, and later completed my higher secondary studies at <strong>St. Joseph’s School, Kuriachira</strong>.
          <br /><br />
          Fueled by curiosity and a love for technology, I’m on a mission to explore the ever-evolving world of computer science. Join me as we dive into this exciting field, sharing knowledge and experiences along the way!
        </p>
      </div>
      <div className="about-photo">
        <motion.img
          src={myPhoto}
          alt="Jose Chemmanoor"
          className="neon-photo"
          initial={{ scale: 0.8, opacity: 0 }}
          animate={{ scale: 1, opacity: 1, transition: { duration: 0.6 } }}
          exit={{ scale: 0.8, opacity: 0, transition: { duration: 0.6 } }}
        />
      </div>
    </motion.section>
  );
};

export default AboutPage;
