import React, { useEffect, useState } from "react";
import "../styles/Home.css";

const titles = ["UI/UX Designer", "Student", "Full-Stack Web Developer"];

const Home = () => {
  const [currentTitle, setCurrentTitle] = useState(titles[0]);

  useEffect(() => {
    const interval = setInterval(() => {
      const randomIndex = Math.floor(Math.random() * titles.length);
      setCurrentTitle(titles[randomIndex]);
    }, 1500); // Changes title every 1.5s
    return () => clearInterval(interval);
  }, []);

  return (
    <section className="home-container">
      <div className="home-content">
        <h2 className="intro-text">Hey there, I'm</h2>
        <h1 className="name">Jose Chemmanoor</h1>
        <h3 className="dynamic-title">{currentTitle}</h3>
      </div>
    </section>
  );
};

export default Home;
