import React, { useState, useEffect } from "react";
import "../styles/Home.css";

function Home() {
  const roles = ["Student", "UI/UX Designer", "Front-End Developer"];
  const [currentRole, setCurrentRole] = useState(roles[0]);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentRole(roles[Math.floor(Math.random() * roles.length)]);
    }, 2000); // Change every 2 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <section className="hero">
      <h2>Hey there, I'm</h2>
      <h1>Jose Chemmanoor</h1>
      <p className="animated-text">{currentRole}</p>
      <div className="btn-container">
        <a href="/about" className="neon-box">Get to Know More About Me</a>
      </div>
    </section>
  );
}

export default Home;
