import React from "react";
import { motion } from "framer-motion";
import "../styles/Education.css";

const educationData = [
  {
    school: "Rajagiri School of Engineering & Technology (RSET)",
    degree: "B.Tech in Computer Science and Engineering",
    year: "2022 - Present",
  },
  {
    school: "St. Joseph’s School, Kuriachira",
    degree: "Higher Secondary Education",
    year: "2020 - 2022",
  },
  {
    school: "Don Bosco ICSE School, Mannuthy",
    degree: "Primary & Secondary Education",
    year: "2008 - 2020",
  },
];

const Education = () => {
  return (
    <motion.div
      className="education-container"
      initial={{ opacity: 0, y: 50 }}
      animate={{ opacity: 1, y: 0, transition: { duration: 0.6 } }}
      exit={{ opacity: 0, y: -50, transition: { duration: 0.6 } }}
    >
      <h1 className="education-heading">Education</h1>
      <div className="timeline">
        {educationData.map((edu, index) => (
          <motion.div
            className="timeline-item"
            key={index}
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0, transition: { duration: 0.6, delay: index * 0.2 } }}
            exit={{ opacity: 0, x: -50, transition: { duration: 0.6 } }}
          >
            <div className="timeline-dot"></div>
            <div className="timeline-content">
              <h3>{edu.school}</h3>
              <p>{edu.degree}</p>
              <span>{edu.year}</span>
            </div>
          </motion.div>
        ))}
      </div>
    </motion.div>
  );
};

export default Education;
