import React from "react";
import "./About.css";
import myPhoto from "../assets/myPhoto.jpg"; // Replace with your actual image path

const About = () => {
  return (
    <div className="about">
      {/* Left Side - Text Section */}
      <div className="about-text">
        <h1 className="about-heading">About Me</h1>
        <p className="about-description">
          Welcome to my digital space! I'm Jose Chemmanoor, a passionate BTech student 
          specializing in Computer Science and Engineering at Rajagiri School of Engineering 
          and Technology (RSET) in Kakkanad, Kochi. While I currently call Kochi home, my journey 
          began in the bustling city of Thrissur, Kerala. 
          <br />
          <br />
          I pursued my early education at Don Bosco ICSE School, Mannuthy, and later completed 
          my higher secondary studies at St. Joseph’s School, Kuriachira. Fueled by curiosity and a 
          love for technology, I’m on a mission to explore the ever-evolving world of computer science. 
          Join me as we dive into this exciting field, sharing knowledge and experiences along the way!
        </p>
      </div>

      {/* Right Side - Photo with Glow Effect */}
      <div className="about-photo-container">
        <img src={myPhoto} alt="Jose Chemmanoor" className="about-photo" />
      </div>
    </div>
  );
};

export default About;
